import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript } from '@react-google-maps/api'; // Import components from the API
import EventThread from './EventThread/EventThread';
import EventItem from './EventItem/EventItem';

function EventDisplay() {
    const [currentItem, setCurrentItem] = useState(null);
    
    const eventsList = [
        {
            description: "Event for druggies to become clean.",
            location: "12345 street ave",
            name: "Cold turkey",
            attendees: 0,
            coordinates: { lat: 40.7128, lng: -74.0060 } // Example coordinates
        },
        {
            description: "Event for alcoholics to become clean.",
            location: "67890 ave",
            name: "More drugs",
            attendees: 0,
            coordinates: { lat: 34.0522, lng: -118.2437 } // Example coordinates
        }
    ];

    const handleItemClick = (event_thread) => {
        setCurrentItem(event_thread); 
    };

    const markerRef = useRef(null);

    const handleMapLoad = (map) => {
        // Create the marker once the map is loaded
        const marker = new google.maps.marker.AdvancedMarkerElement({
            position: { lat: 40.7128, lng: -74.0060 }, // Example coordinates (New York)
            map: map,
            title: "Example Marker"
        });
    };

    return (
        currentItem ? (
            <EventItem
                description={currentItem.description}
                location={currentItem.location}
                name={currentItem.name}
            />
        ) : (
            <div>
                <h3 className='text-5xl my-8'>Events</h3>
                <div className='flex justify-center my-8'>
                    <div className='p-2 mx-6 border border-black rounded-md'>
                        {eventsList.map(event_thread => (
                            <div 
                                key={event_thread.name}
                                onClick={() => handleItemClick(event_thread)} 
                                className='cursor-pointer'
                            >
                                <EventThread 
                                    description={event_thread.description} 
                                    location={event_thread.location} 
                                    name={event_thread.name}
                                />
                            </div>
                        ))}
                    </div>

                    {/* Map Section */}
                    <div className=''>
                        <div className='map-container bg-gray-200 w-1/3 h-screen fixed right-0 top-0'>
                            <h1 className='text-center text-2xl text-gray-700'>Map</h1>

                            {/* Google Map component */}
                            <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
                                <GoogleMap
                                    mapContainerStyle={{
                                        height: "100%",
                                        width: "100%"
                                    }}
                                    center={{
                                        lat: 40.7128, // Example coordinates (New York)
                                        lng: -74.0060
                                    }}
                                    zoom={10}
                                    onLoad={handleMapLoad} // Use onLoad to create the marker
                                >
                                    {/* Other components or elements can be added here */}
                                </GoogleMap>
                            </LoadScript>
                        </div>
                    </div>
                </div>
            </div>
        )
    );
}

export default EventDisplay;
