import { useState } from 'react';
import Profile from '../../Profile/Profile';

function EventItem({ name, description, attendees = 0, location }) {
  const [actionClicked, setActionClicked] = useState(null);
  const [rsvpCount, setRsvpCount] = useState(attendees);
  const [rsvpClicked, setRsvpClicked] = useState(false);

  const handleRsvpClick = () => {
    if (!rsvpClicked) {
      setRsvpCount(rsvpCount + 1);
      setRsvpClicked(true);
    }
  };

  return (
    <div className="p-6 bg-white shadow-lg rounded-lg flex gap-4">
      {actionClicked === 'Profile' ? (
        <Profile />
      ) : (
        <div className="flex-1 space-y-4">
          {/* Event Details */}
          <div>
            <h2 className="font-bold text-2xl">{name}</h2>
            <p className="text-gray-600 mt-2">{description}</p>
          </div>

          {/* About Host */}
          <div
            onClick={() => setActionClicked('Profile')}
            className="cursor-pointer text-indigo-600 font-semibold hover:bg-indigo-100 p-2 rounded"
          >
            About Host
          </div>

          {/* Host Reviews */}
          <div className="border-t pt-4 space-y-2">
            <h2 className="font-semibold text-lg">Host Reviews</h2>
            <div className="space-y-1">
              {/* Review items go here */}
              <p className="text-gray-600">"Great event!" - User 1</p>
              <p className="text-gray-600">"Very informative." - User 2</p>
              {/* Additional reviews as needed */}
            </div>
          </div>

          {/* Location */}
          <div className="text-gray-500 text-sm">
            <p>{location}</p>
          </div>

          {/* Map and RSVP Section */}
          <div className="flex gap-4">
            {/* Map placeholder */}
            <div className="flex-1 h-48 bg-gray-200 rounded-lg">
              <p className="text-center text-gray-500 pt-16">Map (Google Maps)</p>
            </div>

            {/* RSVP Section */}
            <div className="w-32 flex flex-col items-center">
              <button
                onClick={handleRsvpClick}
                className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition-colors"
              >
                RSVP!
              </button>
              <p className="text-xs text-gray-600 mt-1">Attendees: {rsvpCount}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default EventItem;
