/* global google */
import React, { useEffect, useState } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

const EventMap = ({ eventsList }) => {
  const [map, setMap] = useState(null);  // State to store map instance

  useEffect(() => {
    // Initialize the map when the component mounts
    const loader = new Loader({
      apiKey: 'AIzaSyAI9qIEtiKAunSO0KOX7TNvKUdQOAyHxd8', // Replace with your API Key
      version: 'weekly',
      libraries: ['maps', 'marker'],  // Specify the libraries you need
    });

    loader.load().then(async () => {
      // Once the Google Maps API is loaded, create the map
      const { Map, Marker } = await google.maps.importLibrary('maps');
      
      // Initialize the map
      const mapInstance = new Map(document.getElementById('map'), {
        center: { lat: 40.7128, lng: -74.0060 },  // Default center (New York)
        zoom: 10,  // Default zoom level
      });
      setMap(mapInstance);

      // Create a LatLngBounds object to dynamically adjust the map's bounds
      const bounds = new google.maps.LatLngBounds();

      // Add markers to the map and extend the bounds
      eventsList.forEach((event) => {
        const marker = new Marker({
          position: event.coordinates,
          map: mapInstance,
          title: event.name,
          icon: {
            url: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png', // Red pin icon
            scaledSize: new google.maps.Size(32, 32),  // Scale the marker size
          },
        });

        // Extend the bounds to include the marker's position
        bounds.extend(event.coordinates);
      });

      // Adjust the map to fit the bounds of all the markers
      mapInstance.fitBounds(bounds);
    }).catch((e) => {
      console.error('Error loading Google Maps API', e);
    });

    return () => {
      // Cleanup: remove map instance on unmount
      setMap(null);  // Clear the map state when the component is unmounted
    };
  }, [eventsList]);  // Re-run if the events list changes

  return (
    <div className="map-container" style={{ height: '400px', width: '100%' }}>
      <div id="map" style={{ height: '100%', width: '100%' }} />
    </div>
  );
};

export default EventMap;
