/* global google */
import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import EventThread from './EventThread/EventThread';
import EventItem from './EventItem/EventItem';

// Component for displaying the map
const EventMap = ({ eventsList }) => {
    const handleMapLoad = (map) => {
        // Additional map-related logic, if needed
    };

    return (
        <div className="map-container bg-gray-200 w-1/3 h-screen fixed right-0 top-0">
            <h1 className="text-center text-2xl text-gray-700">Map</h1>
            <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" libraries={['marker']}>
                <GoogleMap
                    mapContainerStyle={{ height: "100%", width: "100%" }}
                    center={{ lat: 40.7128, lng: -74.0060 }}
                    zoom={10}
                    onLoad={handleMapLoad}
                >
                    {/* Render markers dynamically */}
                    {eventsList.map((event) => (
                        <Marker
                            key={event.name}
                            position={event.coordinates}
                            title={event.name}
                        />
                    ))}
                </GoogleMap>
            </LoadScript>
        </div>
    );
};

function EventDisplay() {
    const [currentItem, setCurrentItem] = useState(null);

    const eventsList = [
        {
            description: "Event for druggies to become clean.",
            location: "12345 street ave",
            name: "Cold turkey",
            attendees: 0,
            coordinates: { lat: 40.7128, lng: -74.0060 }, // Example coordinates
        },
        {
            description: "Event for alcoholics to become clean.",
            location: "67890 ave",
            name: "More drugs",
            attendees: 0,
            coordinates: { lat: 34.0522, lng: -118.2437 }, // Example coordinates
        },
    ];

    const handleItemClick = (eventThread) => {
        setCurrentItem(eventThread);
    };

    if (currentItem) {
        return (
            <EventItem
                description={currentItem.description}
                location={currentItem.location}
                name={currentItem.name}
            />
        );
    }

    return (
        <div>
            <h3 className="text-5xl my-8">Events</h3>
            <div className="flex justify-center my-8">
                <div className="p-2 mx-6 border border-black rounded-md">
                    {eventsList.map((eventThread) => (
                        <div
                            key={eventThread.name}
                            onClick={() => handleItemClick(eventThread)}
                            className="cursor-pointer"
                        >
                            <EventThread
                                description={eventThread.description}
                                location={eventThread.location}
                                name={eventThread.name}
                            />
                        </div>
                    ))}
                </div>

                {/* Map Section */}
                <EventMap eventsList={eventsList} />
            </div>
        </div>
    );
}

export default EventDisplay;
