import { useState } from 'react';
import Profile from '../../Profile/Profile';

function EventItem({ name, description, attendees=0, location }) {
  const [actionClicked, setActionClicked] = useState(null);
  const [rsvpCount, setRsvpCount] = useState(attendees);
  const [rsvpClicked, setRsvpClicked] = useState(false);


  // Function to handle RSVP click

  const handleRsvpClick = () => {
    if(!rsvpClicked){
    setRsvpCount(rsvpCount + 1); // Update RSVP count state
    setRsvpClicked(true);
  }
};

  return (
    <div>
      {actionClicked === 'Profile' ? (
        <Profile /> // Render the Profile component if actionClicked is 'Profile'
      ) : (
        <div>
          <div>
            <h2 className='font-bold text-xl mb-4'>{name}</h2>
            <p className='my-2'>{description}</p>
          </div>

          <div onClick={() => setActionClicked('Profile')} className='flex float-left justify-left cursor-pointer'>
            <h1>About the Host!</h1>
          </div>
          <div>
            <h2>Host reviews</h2>
          </div>
          <div>
            <h2>{location}</h2>
          </div>
          <div>
            <h2>Map</h2>
          </div>
          <div onClick={handleRsvpClick} className="cursor-pointer">
            <h2>RSVP</h2>
            <p>Attendees: {rsvpCount}</p>
          </div>
        </div>
      )}
    </div>
  );
}


export default EventItem;
